using Microsoft.AspNet.Builder;using Microsoft.Framework.ConfigurationModel;using Microsoft.AspNet.Hosting;using Microsoft.Framework.DependencyInjection;<% if(framework.indexOf('mvc') > -1){ %>using Microsoft.AspNet.Routing;<% } %>namespace <%= projectName %>{    public class Startup    {        public Startup(IHostingEnvironment env)        {            // Setup configuration sources.            Configuration = new Configuration()                .AddJsonFile("config.json")                .AddEnvironmentVariables();        }        public IConfiguration Configuration { get; set; }        public void ConfigureServices(IServiceCollection services)        {            <% if(framework.indexOf('mvc') > -1){ %>            services.AddMvc();            <% } %>        }        public void Configure(IApplicationBuilder app, IHostingEnvironment env)        {            // Add static files to the request pipeline.            app.UseStaticFiles();            <% if(framework.indexOf('mvc') > -1){ %>            app.UseMvc(routes =>            {                routes.MapRoute(                name: "default",                template: "{controller}/{action}/{id?}",                defaults: new { controller = "Home", action = "Index" });            });            <% } %>        }    }}